'ab_format.cpp` <pre><code>#include &lt;iostream&gt;
using namespace std;

int main()
{
    int a, b;
    cin &gt;&gt; a &gt;&gt; b;
    int sum = a + b;
    if (sum &lt; 0) {
        cout &lt;&lt; "-";
        sum = -sum;
    }
    string ans = to_string(sum);
    int    m   = ans.length() % 3;
    for (auto i = 0; i &lt; ans.length(); ++i) {
        cout &lt;&lt; ans[i];
        if ((i - m + 1) % 3 == 0 &amp;&amp; i != ans.length() - 1)
            cout &lt;&lt; ',';
    }
    return 0;
}
</code></pre>
